INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 13, 3, 3, 'A le souci d''obtenir un travail correct. Cherche à s''améliorer.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 14, 3, 4, 'Toujours satisfaisante. A le sens et le souci de la qualité.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 15, 3, 5, 'Excellente. S''emploie à atteindre tous les objectifs fixés.');
