INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 17, 4, 2, 'N''arrive pas toujours à l''heure. A souvent besoin de s''absenter.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 18, 4, 3, 'Satisfaisante.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 19, 4, 4, 'Ponctuel. Prolonge son temps de travail sur demande.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 20, 4, 5, 'Particulièrement disponible quels que soient les horaires imposés.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 21, 5, 1, 'Ne s''intègre pas. Peu respectueux de la hiérarchie. Esprit contestataire.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 22, 5, 2, 'Fait tout juste ce qui lui est demandé. N''a pas le sens du travail d''équipe.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 23, 5, 3, 'Correct avec tous. Bon équipier.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 24, 5, 4, 'Respectueux des ordres et directives donnés. Responsable. Déterminé.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 25, 5, 5, 'Excellent en toutes circonstances. Aime son mérier et l''exécute avec enthousiasme.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 26, 6, 1, 'Ne prend jamais d''initiative même si nécessaire à la réalisation de ses tâches.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 27, 6, 2, 'N''aime pas et fuit les responsabilités. Ne prend jamais de décision.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 28, 6, 3, 'Normale. Capable d''autonomie.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 29, 6, 4, 'Accepte les responsabilités. Capable de prendre des décisions judicieuses et réalistes.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 30, 6, 5, 'Aime les responsabilités. Esprit de décision. Initiatives heureuses et intelligentes.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 31, 7, 1, 'Manque de prévoyance et d''organisation.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 32, 7, 2, 'Perd beaucoup de temps. A du mal à choisir ses priorités d''action.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 33, 7, 3, 'Coordonne bien ses activités.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 34, 7, 4, 'Sait s''organiser, choisir les priorités. Agit avec méthodes.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 35, 7, 5, 'Soucieux de l''efficacité et des besoins de tous. Sait distinguer l''essentiel et l''accessoire et organiser son travail en conséquence.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 36, 8, 1, 'Ne contrôle jamais les résultats de son action. Attend que d''autres le fassent.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 37, 8, 2, 'Contrôle peu les résultats de son action.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 38, 8, 3, 'Vérifie bien ses résultats.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 39, 8, 4, 'Sait bien contrôler son travail et apporter les corrections nécessaires.');
INSERT INTO CompetenceLevels( CompetenceLevelId, CompetenceId, LevelId, Description ) VALUES ( 40, 8, 5, 'Contrôle continuellement l''avancement et la qualité de ses travaux. A le goût de la perfection.');
